full_solution:
print("Event RSVP Manager")
guests = {}
all_dietary_restrictions = set()
guests["Alice"] = {"status": "attending", "meal": "vegetarian", "restrictions": {"gluten-free", "dairy-free"}}
guests["Bob"] = {"status": "attending", "meal": "chicken", "restrictions": {"dairy-free"}}
guests["Charlie"] = {"status": "not attending", "meal": "none", "restrictions": set()}
guests["Diana"] = {"status": "attending", "meal": "fish", "restrictions": {"gluten-free", "shellfish"}}
print()
print("Guest List:")
for name, info in guests.items():
    print(name + " - " + info["status"])
print()
print("Attending Guests Meals:")
for name, info in guests.items():
    if info["status"] == "attending":
        print(name + ": " + info["meal"])
for guest_info in guests.values():
    all_dietary_restrictions.update(guest_info["restrictions"])
print()
print("All Dietary Restrictions:")
print(sorted(all_dietary_restrictions))
common_restrictions = guests["Alice"]["restrictions"] & guests["Diana"]["restrictions"]
print()
print("Common restrictions (Alice & Diana):")
print(sorted(common_restrictions))
attending_count = 0
for guest_info in guests.values():
    if guest_info["status"] == "attending":
        attending_count += 1
print()
print("Total attending: " + str(attending_count))
