full_solution:
class Pet:
    def __init__(self, name, age):
        self.name = name
        self.age = age
    def info(self):
        print(f"Name: {self.name}, Age: {self.age}")
class Dog(Pet):
    pass
    def bark(self):
        print(f"Woof! I am {self.name}!")
my_dog = Dog("Buddy", 3)
my_dog.info()
my_dog.bark()
my_cat = Pet("Whiskers", 2)
my_cat.info()
