full_solution:
class Character:
    def __init__(self, name, health):
        self.name = name
        self.health = health
    def display_stats(self):
        print(f"Name: {self.name}")
        print(f"Health: {self.health}")
    def attack(self):
        print(f"{self.name} attacks for 10 damage!")
hero = Character("Aria", 100)
hero.display_stats()
hero.attack()
class Warrior(Character):
    pass
    def special_attack(self):
        print(f"{self.name} uses Power Strike for 25 damage!")
fighter = Warrior("Brutus", 150)
fighter.display_stats()
fighter.attack()
fighter.special_attack()
